raw.css:
.bar {
  border: solid 2px white;
  margin-bottom: 5px;
  width: 36px;
  transition: 0.5s;
}
#naav {
  transition: 0.5s;
}
.click #naav {
  transform: translate(0, 320px);
}
.click .hamburger__bar1 {
  transform: rotate(45deg) translate(0, 12px);
}

.click .hamburger__bar2 {
  opacity: 0;
}

.click .hamburger__bar3 {
  transform: rotate(-45deg) translate(0, -12px);
}

